import "./Header.css";
import logo from "../../images/logo.svg";
import logoAcc from "../../images/logoAcc.svg";
import otherLogoAcc from "../../images/otherLogoAcc.svg";
import { Link, useLocation } from "react-router-dom";
import React, { useState, useEffect } from "react";

function Header() {
  const [isMainPage, setIsMainPage] = useState(false);
  const location = useLocation();

  useEffect(() => {
    setIsMainPage(location.pathname === "/");
  }, [location]);

  return (
    <div className="header">
      <div className="header__logo">
        <Link to="/">
          <img src={logo} className="header__logo-image" alt="Logo" />
        </Link>
      </div>
      <div className="header__profile-link">
        <button className="header__profile-button">
          <img src={isMainPage ? logoAcc : otherLogoAcc} className="header__logo-account" alt="Account Logo" />
        </button>
      </div>
    </div>
  );
}

export default Header;
